function getRequests (){

axios.get("http://asdfast.beobit.net/api").then((res)=>{
console.log(res)
setData(res);
},(err) => {
console.log(err)});
}

function getRequestb (e){
    e.preventDefault();
     axios.get("https://jsonplaceholder.typicode.com/users").then((res)=>{
    console.log(res)
    setData1(res);
    return res
    },(err) => {
    console.log(err)
    return err

});
}


function pintar (){
    var cont=0;
    return data1.map((u,index,array)=>  {

while (cont<5 && array.indexOf(u)<=2){
cont++;
return (
   <div>
    <span > 
   ${cont}
    </span>

    <h2> ${u[index].id} </h2>


    </div>
      );
}
         });
}

useEffect(() => {
    getRequests();
    }, [])


return (

<div>   
<> 
 <title>  getreq simple  cuando carga la página   </title>
 <p> ${data}</p>
 </>   



<>  <title>  botón getreq render dinamico </title> 
<button onClick={e => getRequestb(e)}>Pintar</button>
{data1!= null? pintar(): null}
</> 


 </div>
);

}
